"use strict";(self.webpackChunkwebsite=self.webpackChunkwebsite||[]).push([[9984],{3074:e=>{e.exports=JSON.parse('{"tag":{"label":"ldap","permalink":"/docs/tags/ldap","allTagsPath":"/docs/tags","count":2,"items":[{"id":"saml-ldap-authentication","title":"Use Both SAML and LDAP for TeamForge User Authentication","description":"By setting up the SAML+LDAP IdP, TeamForge users can reap the benefits of both SAML and LDAP authentication mechanisms in a unified manner. With SAML+LDAP authentication, while SAML enables TeamForge users to access web applications, the LDAP authentication supports user authentication required for CLI applications. For example, if a user performs a source code commit in Git/SVN repository, the user can get authenticated via LDAP.","permalink":"/docs/saml-ldap-authentication"},{"id":"ldap-authentication","title":"Use LDAP for TeamForge User Authentication","description":"TeamForge supports integration with LDAP. Once integrated with LDAP servers, TeamForge can use LDAP credentials for user authentication.","permalink":"/docs/ldap-authentication"}],"unlisted":false}}')}}]);